import React, { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const nav = useNavigate();

  const [email, setemail] = useState("pawan");
  const [password, setpassword] = useState("pawan");

  // const getp = async () => {
  //    const res = await axios.post("http://localhost:4010/login",{email,password});
  //     console.log(res)
  //     // localStorage.setItem("user", JSON.stringify(res.data));
  // //  const a=localStorage.getItem("user")
  // //    return a?nav("/"):console.log("error")
  //   };

  const abc = async () => {
    const res = await axios.post("http://localhost:4010/login", {email,  password, });
    if (res) { nav("/")}
    else { alert("Invalid email or password!");}


    localStorage.setItem("user", JSON.stringify(res.data));
  };

  useEffect(() => {
    const a= localStorage.getItem("user");
    if(a){nav("/")} 
  },[nav]);

  return (
    <div className="bg-black  ">
      <form class="max-w-sm mx-auto h-screen pt-45">
        <div class="mb-5">
          <label
            for="email"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Your email
          </label>
          <input
            value={email}
            onChange={(e) => setemail(e.target.value)}
            type="email"
            id="email"
            class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
            placeholder="name@flowbite.com"
            required
          />
        </div>
        <div class="mb-5">
          <label
            for="password"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Your password
          </label>
          <input
            value={password}
            onChange={(e) => setpassword(e.target.value)}
            type="password"
            id="password"
            class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
            required
          />
        </div>

        <button
          type="sumbit"
          onClick={abc}
          class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm w-full sm:w-auto px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default Login;
