import React, { useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const nav = useNavigate();

  const getp = async () => {
    try {
      const res = await axios.get("http://localhost:4010/login");

      // Save to localStorage
      localStorage.setItem("user", JSON.stringify(res.data));

      // Navigate if user exists
      const a = localStorage.getItem("user");
      if (a) {
        nav("/"); // ✅ Navigate to home
      } else {
        console.log("error");
      }

    } catch (err) {
      console.error("Login failed", err);
    }
  };

  useEffect(() => {
    getp();
  }, [nav]); // ✅ Clean, no warning

  return (
    <div className="bg-black">
      <form className="max-w-sm mx-auto h-screen pt-45">
        <div className="mb-5">
          <label htmlFor="email" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">
            Your email
          </label>
          <input
            type="email"
            id="email"
            className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600"
            placeholder="name@flowbite.com"
            required
          />
        </div>
        <div className="mb-5">
          <label htmlFor="password" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">
            Your password
          </label>
          <input
            type="password"
            id="password"
            className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600"
            required
          />
        </div>
        <button
          type="submit"
          className="text-white bg-blue-700 hover:bg-blue-800 font-medium rounded-lg text-sm w-full px-5 py-2.5 text-center"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default Login;
