import React from "react";

const movies = [
  {
    title: "Inception",
    description: "A mind-bending thriller by Christopher Nolan.",
    imageUrl: "https://i.imgur.com/8UGaR1b.jpg",
  },
  {
    title: "Interstellar",
    description: "Exploring time and space with deep emotions.",
    imageUrl: "https://i.imgur.com/EuC3kAn.jpg",
  },
  {
    title: "Avengers: Endgame",
    description: "The ultimate battle of the Marvel universe.",
    imageUrl: "https://i.imgur.com/YOwpSxN.jpg",
  },
];

const MovieCard = ({ title, description, imageUrl }) => {
  return (
    <div className="bg-white rounded-2xl shadow-lg overflow-hidden w-full sm:w-[300px] hover:scale-105 transition-all">
      <img src={imageUrl} alt={title} className="w-full h-48 object-cover" />
      <div className="p-4">
        <h2 className="text-xl font-bold text-gray-800">{title}</h2>
        <p className="text-sm text-gray-600 mt-2">{description}</p>
        <button className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-all">
          Watch Now
        </button>
      </div>
    </div>
  );
};

const Home = () => {
  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-center mb-8">ðŸŽ¬ Movie Zone</h1>
      <div className="flex flex-wrap gap-6 justify-center">
        {movies.map((movie, index) => (
          <MovieCard
            key={index}
            title={movie.title}
            description={movie.description}
            imageUrl={movie.imageUrl}
          />
        ))}
      </div>
    </div>
  );
};

export default MovieLit;
